INSERT INTO `stafProfiel` (`id`, `naam`, `verhaaltje`, `laatst_bijgewerkt`, `groep`) VALUES
(1, 'Jasper', 'Ik ben Jasper, 20 jaar oud en naast het bezig houden van Bevers zorg ik ook dat er een website voor de BV is.\r\n\r\nIk ben pas sinds 2011 actief bij de BV als staflid maar daarvoor was ik lid en staflid bij de zuster vereniging van de BV.', '2013-03-17 15:28:30', 'bevers'),
(2, 'Kim', 'Hoi, ik ben Kim, ik ben 23 jaar en dit jaar is alweer mijn 5e jaar bij de bevers. Ik vind het erg leuk om staflid bij de bevers te zijn, omdat de kinderen altijd enthousiast reageren op de gekke programma''s die wij bedenken, omdat wij dus ook vaak gekke dingen doen en omdat het altijd gezellig is. ', '2013-09-18 11:00:22', 'bevers'),
(3, 'Lex', 'Mijn naam is Lex en ik ben 19 jaar oud. Ik ben al drie jaar staf van de bevers. Mijn hobby''s zijn scouting natuurlijk, drummen en hardlopen. Mijn lievelingseten is pasta en dit komt mooi uit want ik ben pastakok. Mijn droom is om veel te reizen en leuke mensen te leren kennen. Ik ben staf bij scouting omdat ik het erg leuk vindt om in een gezellige sfeer creatieve programma''s voor kinderen te bedenken en te zien dat de kinderen er ook van genieten! ', '2013-09-13 11:50:35', 'mercators'),
(4, 'Bram', '', '2014-01-07 13:21:54', 'archief'),
(5, 'Koen', 'Ik ben Koen, ik ben 16 jaar en nog maar net begonnen met het leiden van de bevers. ik heb heel veel plezier in het maken en uitvoeren van de fantasierijke programma''s voor de bevers. ', '2013-03-08 13:57:41', 'bevers'),
(6, 'Maarten', 'Ik ben 18 jaar oud. Ik heb van kinds af aan al op scouting gezeten. en omdat het veel te leuk is ben ik nu staflid. Dit jaar draai ik de esta''s en dat is super leuk!', '2013-03-08 10:56:04', 'estas'),
(7, 'Linda', 'Hallo!\r\n\r\nMijn naam is Linda en ik ben 17 jaar oud.\r\nIk ben nu drie jaar lid bij de BV en heb het erg naar mijn zin.\r\nDit is mijn tweede jaar als staflid en eerste jaar als bever staflid. Ik heb zin in het aankomende jaar bij de bevers!\r\nGroetjes, Linda', '2013-09-16 07:13:17', 'bevers'),
(8, 'Femke', 'Hallo!\r\n\r\nIk ben zelf als lid begonnen met scouting bij de esta''s en nu ben ik esta staflid. Ik wil zeker nog een tijdje doorgaan want ik vind het super leuk! \r\n', '2013-10-08 21:00:45', 'estas'),
(24, 'Hester', '', '2013-09-13 11:53:11', 'estas'),
(25, 'Vera', 'Hallo, \r\n\r\nIk ben Vera en zit bij de r/s.\r\nIk ben 3 jaar geleden bij deze scouting gekomen en ik heb het hier erg naar mijn zin!\r\nDit is mijn eerste jaar als staf en ik hoop dat het erg leuk wordt.\r\n\r\nGroetjes', '2013-09-20 07:27:50', 'estas'),
(11, 'Irina', 'Hallo!\r\n\r\nVoor mijn studie op de Pabo moest ik studiepunten halen d.m.v. vrijwilligerswerk. Zo ben ik bij Brigitta Valentijn beland. Ik had het zo naar mijn zin dat ik maar ben blijven ''plakken'' :-)\r\nDit is mijn tweede jaar als Esta staf.', '2013-09-19 18:23:57', 'estas'),
(12, 'M-P', 'Doordeweeks werkend als reparateur van IT-producten, maar in het weekend wil hij niets met computers te maken hebben! Dit is Maarten-Paul, gek op motor en autorijden en als ze stilstaan het poetsen er van.\r\nMet scouting wij hij zo min mogelijk technologie en zo veel mogelijk overleven, hoe slechter de omstandigheden (weer, voorzieningen) des te beter! (Hoewel... zon, zee en strand zijn ook geen verkeerde keuzes)', '2013-09-24 21:25:47', 'rs'),
(13, 'Daan', '', '2013-02-26 15:12:43', 'mercators'),
(15, 'Jordi', '', '2013-02-26 15:13:35', 'mercators'),
(16, 'Alesha', 'Ik ben Alesha, 22 jaar oud en studeer in juni af aan de PABO. Ik werk dus al dagelijks met kinderen en vind het hiernaast leuk om ook in het weekend met kinderen bezig te zijn!', '2013-03-01 22:32:16', 'mercators'),
(17, 'Nora', 'Nora kwam als Esta bij de BV en ze studeert nu aan de sociale academie.', '2013-11-09 10:47:42', 'rs'),
(18, 'Misja', 'Misja is bij de RS beter bekend als Dikke! Mooiste BV moment in eigen woorden: Overal waar een kampvuur was, zolang het er niet regent!', '2013-09-13 11:51:26', 'pivos'),
(19, 'Micha', 'Roep heel hard "Duns", goed kijken, en je ziet dat hij voor je staat.\r\nMicha''s lievelingsdier is een cavia, meer hoef je niet van hem te weten!', '2013-09-13 11:51:29', 'pivos'),
(23, 'Gijs', 'Hallo!\r\n\r\nNa 7 jaar als lid bij de BV leek het me erg leuk om ook staf te worden, zo ben ik terecht gekomen als staflid bij de esta''s. Ik hoop dat m''n eerste jaar erg leuk gaat worden en dat ik het nog lang zal blijven volhouden!\r\n\r\nGroetjes ', '2013-09-29 14:50:04', 'estas'),
(26, 'Anne', '', '2014-01-07 13:22:33', 'bevers'),
(27, 'Olivier', '', '2014-01-07 13:22:33', 'bevers');
INSERT INTO `subpaginas` (`id`, `hoofdpagina_id`, `titel`, `linktitel`, `linknaam`, `inhoud`, `html`, `gewijzigd_op`) VALUES
(1, 2, 'De Bevers', 'De Bevers (5 t/m 7 jaar)', 'bevers', 'De Bevers zijn een leuke en actieve groep voor kinderen tussen de 5 en 8 jaar. We doen verschillende activiteiten zoals knutselen, speurtochten, toneel, sporten en andere leuke spelletjes. Om de zoveel tijd neemt een figuur ons mee in een fantasie wereld waar wij op een creatieve, enthousiaste en grappige manier een avontuur beleven.\r\n\r\nDe Bevers komen elke zaterdag van 12:30 tot 14:30 bij elkaar op de Mient 351 (ingang hek naast de Vlierboomschool).\r\n\r\nVerder gaan wij 2x per jaar op weekend en 1x per jaar 4 dagen op zomerkamp. Tijdens de weekenden en kampen verblijven wij in een blokhut.\r\n\r\nVoor vragen kan er gemaild worden naar:\r\nbevers@brigittavalentijn.nl', 0, '2013-04-30 13:36:42'),
(2, 2, 'De Esta''s', 'De Esta''s (8 t/m 10 jaar)', 'estas', 'De Esta’s is de groep voor kinderen van 8 t/m 10 jaar. De zaterdagmiddagen van de Esta’s bestaan uit een spelaanbod vol sport, toneel, ontdekking en creativiteit. Elke keer weer aan de hand van een leuke, fantasievolle en grappige thema’s. \r\n\r\n\r\nWat doen zij nog meer?\r\n\r\n2/3 keer per jaar een weekendje weg\r\n1x per jaar één week op kamp in de zomervakantie. \r\nTijdens deze nacht(jes) weg verblijven de kinderen in een blokhut (huisje) van een andere scoutingvereniging. Regelmatig krijgen zij dan bezoek van fantasiefiguren die hen meeslepen in spannende avonturen. Deze activiteiten zitten bij de contributie inbegrepen.  \r\n\r\nDe Esta''s draaien op zaterdag van 12:30 tot 14:30 uur.\r\nU kunt contact met ons opnemen door te mailen naar: estas@brigittavalentijn.nl', 0, '2013-05-03 12:28:39'),
(3, 2, 'De Mercators', 'De Mercators (11 t/m 13 jaar)', 'mercators', 'Deze naam is gegeven aan de gidsen/verkenners-groep. De activiteiten van deze groep worden uitgebreid met kamperen, fietsen, pionieren (met hout en touw werken), kaart- & kompastechnieken, koken, enzovoorts.\n\nNaarmate de kinderen ouder worden krijgen zij een steeds grotere inbreng in het programma en bepalen zij steeds meer de interessevelden waarin gespeeld wordt.\n\nDe Mercators draaien op Zaterdag van 12:30 tot 14:30 op de Mient.\nU kunt contact met ons opnemen door te mailen naar: mercators@brigittavalentijn.nl', 0, '2013-06-24 15:42:28'),
(4, 2, 'Rowans en Sherpa''s', 'De R/S (14 t/m 17 jaar)', 'rs', 'De Rowans en Sherpa''s gaan de technieken die zij bij de Mercator hebben geleerd, in de praktijk toepassen door middel van hikes (trektochten), droppingen, projecten, discussies, enzovoorts.\n\nDaarnaast komt de nadruk steeds meer te liggen op een grotere zelfstandigheid: zelf programma''s maken, zelf organiseren. Uiteindelijk is het de bedoeling dat de groep draait onder zelfbestuur van de kinderen. De leiding wordt langzaam maar zeker begeleiding.\n', 0, '2013-03-21 20:38:37'),
(5, 2, 'De Pivo''s', 'De Pivo''s (18 t/m 23 jaar)', 'pivos', 'De Pivo''s bestaat uit een groep jongeren die zelfstandig draaien onder begeleiding van stafleden die niet altijd meer aanwezig hoeven te zijn. Alle programma''s, maar ook alle organisatorische zaken worden door hen zelf geregeld. Vaak zijn een aantal leden van de Pivo''s ook al staflid bij één van de andere leeftijdsgroepen.\n\nDe Pivos draaien op Zaterdag van 19:30 tot 21:30 op de Mient.\nU kunt contact met ons opnemen door te mailen naar: pivos@brigittavalentijn.nl', 0, '2013-06-24 15:42:52'),
(6, 2, 'De Stam', 'De Stam (23 jaar en ouder)', 'stam', 'De Stamgroep is voor alle mensen die maar geen genoeg kunnen krijgen van de Brigitta Valentijn. En dit zijn er toch aardig wat, want de Stamgroep is al jaren de grootste groep van de BV! Iedereen die de wekelijks draaiende groepen van de BV is ontgroeid (vanaf een jaar of 20) is welkom bij de Stam. Er wordt niet meer elke week gedraaid, maar er worden toch regelmatig leuke activiteiten georganiseerd waar ieder lid zich voor kan inschrijven. Voorbeelden van activiteiten zijn poolen, kamperen, uitgaan en samen feestdagen vieren.\r\n\r\nBij de Stamgroep zitten een aantal oude rotten die al rond de 20 jaar lid zijn, maar er komen ook nog steeds regelmatig nieuwe leden bij. Een aantal van de Stamleden zijn ook staflid, maar niet allemaal. Al met al is het een goed gezelschap om de oude dag mee door te brengen!', 0, '2013-12-18 22:10:47'),
(7, 4, 'Algemene informatie over de Brigitta Valentijn', 'Algemene Informatie', 'algemeneInformatie', 'Scouting Brigitta Valentijn (of kort gezegd: ''de BV'') is een eigentijdse, gemengde Scouting-groep uit Den Haag met enthousiaste leiding, die geheel uit vrijwilligers bestaat. We zijn voornamelijk bezig met Knutselen, Creativiteit, Fantasie en Sportiviteit.  Elke week is anders en lang leven de LOL! Is ons motto. Iedere zaterdag komen wij met de kinderen van 5 jaar tot 18+ op de Mient bij elkaar.\n\nMomenteel hebben we ongeveer 100 jeugdleden. De activiteiten worden verzorgd door ongeveer 20 vrijwilligers die elke zaterdag de activiteiten uitvoeren. Daarnaast is er een bestuur die de vereniging bestuurt. Wil je weten wie wat doet en weten wie wie is, kijk dan bij Onze Helden . Naast deze vrijwilligers, zijn er ook nog mensen waar we sinds jaar en dag op kunnen rekenen. Zij zorgen voor de extraatjes. Rijden soms naar kampeerterreinen, maken mooie pakken voor fantasiespellen, kopieëren het clubblaadje, etc. Al met al houden dus zo''n 135 mensen zich actief bezig met de BV en daar zijn we best trots op!\n\nDe Bevers (5 t/m 7 jaar), de Esta''s (7 t/m 11 jaar) en de Mercators (11 t/m 14 jaar) zijn van 12.30-14.30 in de Lijsterbes te vinden. \nDe Rowans/Sherpa''s (14 t/m 17 jaar) en de Pivo''s (17+) komen op de zaterdagavond van 19.30 tot 21.30 uur bij elkaar. De Stamgroep draait op onregelmatige tijden. Elke leeftijdsgroep gebruikt daarvoor een eigen ruimte.\n\nEr worden allerlei activiteiten ondernomen, zoals sport & spel, toneel, creativiteitsspelen, scoutingtechnieken (bijvoorbeeld kaart & kompas, pionieren), buiten spelen, enz. Spelenderwijs leren de kinderen op die manier wat ze aan elkaar hebben en leren ze met elkaar om te gaan. \nDe nadruk van het spel ligt niet op competitie, maar op samenwerking: Lang leve de Lol!.\n\nWij hopen hierdoor een kleine bijdrage te kunnen leveren aan een positieve ontwikkeling van uw kind.\n\nDe BV maakt geen gebruik van het traditionele scouting uniform. Dus: geen baretten, deukhoeden, dassen en insignes.\n\nContact:\nChantal Noest - bestuur@brigittavalentijn.nl\n\n\nScouting Brigitta/Valentijn\nMient 351, 2564 LA Den Haag\ne-mail: bestuur@brigittavalentijn.nl,\ninternet: www.brigittavalentijn.nl', 0, '2013-06-24 15:44:01'),
(8, 4, 'Activiteiten', 'Activiteiten', 'activiteiten', '<h2>Weekenden:</h2>\nOm wat aan het kamp te wennen gaan de groepen een paar keer per jaar een weekendje weg. De jongste speltakken overnachten in een blokhut, vanaf 11 jaar wordt er gekampeerd.<br />\n<br />\n<h2>Het overvliegfeest:</h2>\nIn september wisselen de oudste kinderen van leeftijdsgroep. Dit wordt met de hele groep gevierd.<br />\n<br />\n<h2>De Boerenkooltocht:</h2>\nEen winteractiviteit in februari, waarbij na afloop met de hele groep boerenkool wordt gegeten.<br />\n<br />\n<h2>Het Grote Groeps Weekend:</h2>\nEen 2-jaarlijks kampeerweekend, waarbij behalve alle kinderen ook alle ouders, broertjes en zusjes meekunnen.<br />\n<br />\n<h2>Het Zomerkamp:</h2>\nHet hoogtepunt van ieder jaar (seizoen) is het zomerkamp. Het zomerkamp is altijd in de eerste of tweede week van de zomervakantie. Tijdens het kamp wordt er in een thema gedraaid. Dit thema verschilt per leeftijdsgroep. De Esta''s slapen op kamp in een boerderij, de Mercator gaat kamperen in een bos, de Rowans/Sherpa''s, Pivo''s en Stam trekken meestal rond.<br />\n<br />\nUiteraard is deze week niet gratis, maar u betaalt het kampgeld apart. Halverwege het jaar ontvangt u bericht hoeveel het kamp kost en wanneer het geld uiterlijk binnen moet zijn.<br />\n<br />\nDe kosten van het zomerkamp zijn:<br />\nBevers:                        &euro;75,00<br />\nEsta''s en Mercators:       &euro;115,00<br />\nR/S:                             &euro;125,00<br />\nPivo''s:                          &euro;130,00<br />\n', 1, '2012-10-07 11:22:03'),
(9, 4, 'Contributie', 'Contributie', 'contributie', '<br>\nVoor iedere maand dat uw kind lid is, betaalt u maandelijkse contributie.<br> Deze contributie is incl. geld voor weekenden en het zomerkamp.<br>\n<br>\nDe contributie bedraagt voor;<br>\n<br>\nBevers : &euro; 19.20<br>\nEsta''s : &euro; 23.45<br>\nMercators : &euro; 23.45<br>\nRowans/Sherpas : &euro; 25.05<br>\nPivo''s : &euro; 25.05<br>\nStam : &euro; 6.20<br>\n<br>\nWij verzoeken u de contributie te betalen via automatische incasso. Als u op een andere manier wilt betalen of andere vragen heeft over financi&euml;le zaken heeft, mail dan naar <a>bestuur@brigittavalentijn.nl</a><br>\nOns banknummer is: 156229161 o.n.v Scouting Brigitta Valentijn te Den Haag.\nMocht u willen opzeggen dan geldt er een opzegtermijn van 1 kalenderjaar en dit dient schriftelijk te gebeuren, bij het bestuur. ', 1, '2013-06-24 15:44:28'),
(10, 2, 'De Esta''s doen Klusjes', 'De Esta''s (8 t/m 10 jaar)', 'estaklusje', 'De esta''s gaan ieder jaar op kamp. Dit jaar komt daar ggw ook nog bij. Kortom we moeten ons best doen om genoeg geld te verzamelen om dit kamp te kunnen draaien! Daarom hebben de esta''s dit jaar een leuke financiële actie bedacht.\n\nHelaas is de contributie die wij vragen niet genoeg om een kamp van te draaien. Er zijn genoeg waardeloze loten waarmee je geld binnen haalt voor je club. Maar eigenlijk staan deze gelijk aan een verhoging van de contributie. Daarom gaan we dit jaar op zoek naar een financiële actie waar je ook wat aan overhoud.\n\nDaarom bieden de esta''s de volgende diensten aan:\n\nBuiten klusjes	\n				\nStraat/sneeuw vegen					1,- \nBalkon/tuin opruimen					2,-\nIemand naar scouting meenemen, de staf betaald 		0,50\nDe schuur opruimen					2,-\nEen auto/fiets wassen					5,-/2,5\nportiek schoonmaken					1,5\nGlas wegbregen					        1,- per tas\nPapier wegbrengen					1,- per tas\nLege flessen inleveren					1,- per tas\nSchoenen poetsen/schoonmaken				0,50\nBoodschappen doen					2,-\n\nBinnen klusjes			\n\nEen dierenkooi/bak verschonen			        2,-\nDe afwas doen of de vaatwasser inruimen			1,-\nThee of koffie zetten					0,5\nHelpen met koken					1,-\nFruit snijden voor iemand				0,50\nIemand helpen met huiswerk maken			0,50\nAan iemand voorlezen 					0,50\nIemand een ontbijt op bed geven				1,-\nDe planten water geven					0,5\nDe WC schoonmaken					2,-\nHelpen met de was ophangen				1,-\nHelpen met de was vouwen en opruimen			1,-\nDe kamer vegen/stofzuigen				1,-\n\n\n\nDe bedoeling is dat je je kan inschrijven voor de klussen die we aanbieden.\nVoor deze klussen geld de prijs die wij hier voor vragen.\nJe kunt je dus aanmelden voor één of meer klussen die hier boven staan aangegeven. Deze klus wordt dan door de esta''s zelf uitgevoerd. \nHet bedrag dat voor de klus is afgesproken kan dan in de grote pot voor ons geweldige kamp. hierdoor steun je toch het zomerkamp en is bijvoorbeeld je auto weer blinkend schoon.\n\nBen je geïnteresseerd? Of denk je dat je buurvrouw wel wat hulp kan gebruiken.\nMail dan naar estas@brigittavalentijn.nl\nJe zult dan zo snel mogelijk meer informatie ontvangen!\n\nGroetjes de Esta''s', 0, '2013-06-24 15:44:46'),
(12, 4, 'Ooievaarspas', 'Ooievaarspas', 'ooievaarspas', 'Bij de Brigitta Valentijn kunt u ook terecht met een Ooievaarspas, en vanaf nu is het al mogelijk om de Ooievaarspas van 2012 aan te vragen. Voorkom wachten, dus vraag de Ooievaarspas 2012 NU aan! Met een Ooievaarspas krijgt u bij ons wel tot meer dan 50% korting!<br />\n<br />\nU krijgt de Ooievaarspas 2012 niet vanzelf thuis gestuurd. U kunt deze pas nu al aanvragen. Dan weet u zeker dat u in het nieuwe jaar niet lang op uw nieuwe pas hoeft te wachten.<br />\n<br />\n<strong>Hoe?</strong><br />\nU kunt de Ooievaarspas 2012 op twee manieren aanvragen:<br />\n<br />\nOp www.denhaagopmaat.nl/ooievaarspas<br />\nBij het Advies en Informatiepunt in uw stadsdeelkantoor<br />\n<br />\nHeeft u een WWB-uitkering van de Dienst SZW-Den Haag? Dan hoeft u geen aanvraag in te dienen!<br />\nPashouders die in Leidschendam-Voorburg en Rijswijk wonen kunnen contact opnemen met de eigen gemeente.<br />\n<br />\nU ontvangt uw Ooievaarspas 2011 nog niet direct, we verwerken alvast uw aanvraag.<br />\nVoor informatie kunt u bellen: <strong>070-353 7500</strong>', 1, '2013-06-24 15:44:58'),
(13, 4, 'Een keertje komen kijken', 'Komen kijken', 'keertjekijken', '<p>Lijkt het jou leuk om een keertje te komen kijken bij onze gezellige scouting vereniging?<br />\nDat mag natuurlijk altijd!<br />\n<br />\nDe draai-tijden staan per groep op de groeps-pagina. Verder draaien alle groepen in principe op de Mient. <br />\n<br />\nNaast dat het voor de groepen handig is om te weten of je komt kijken zodat ze rekening kunnen houden met het programma dat er een extra persoon is kan het natuurlijk een keer voorkomen dat een bepaalde groep op een andere locatie draait voor die ene Zaterdag.<br />\nOm deze redenen zouden we graag willen vragen of je een mailtje wilt sturen naar de betreffende groep om te zeggen dat het je leuk lijkt om een keertje te komen kijken.<br />\n<br />\nDe email adressen zijn als volgt:<br />\n<br />\n<strong>Bevers</strong>: bevers@brigittavalentijn.nl<br />\n<strong>Esta''s</strong>: estas@brigittavalentijn.nl<br />\n<strong>Mercators</strong>: mercators@brigittavalentijn.nl<br />\n<strong>R/S</strong>: rs@brigittavalentijn.nl<br />\n<strong>Pivos</strong>: pivos@brigittavalentijn.nl<br />\n<br />\nGraag zien we je dan een keertje op een Zaterdag langskomen.<br /></p>', 1, '2013-06-24 15:45:07'),
(14, 8, 'Kabouter Wouter', 'Kabouter Wouter', 'kabouterwouter', '<iframe width="560" height="315" src="http://www.youtube.com/embed/ojG5Aacce-U" frameborder="0" allowfullscreen></iframe>\r\n<iframe width="560" height="315" src="http://www.youtube.com/embed/2UzOhUHXjvw" frameborder="0" allowfullscreen></iframe>\r\n<iframe width="560" height="315" src="http://www.youtube.com/embed/6_SXIzwrJDk" frameborder="0" allowfullscreen></iframe>', 1, '2013-08-01 13:38:13'),
